INSERT INTO `sb_job` VALUES (66, 34, 'บ้านเช่าราคาถูก', 'เป็นทาวเฮ้าชั้นเดียว อยู่แพรกษาซ.9 หมู่บ้านแสงปัญญา หลังหมู่บ้านติดสวนสาธารณะ ทะลุถ.เทพารักษ์ได้ เข้าได้ 3 ทาง\r\nบ้านเป็นบ้านมุม ลมผ่านเย็นสบายทั้งวัน มีของกินตลอดหากินง่าย เหมาะสำหรับคนที่ทำงานแถวสมุทรปราการ นิคมอุสหกรรมบางปู มีสองแถวไปบิ๊กซีกิ่งแก้ว และออกสำโรง อยู่กันเป็นครอบครัว หรือรวมกันก็ได้ เพราะแบ่งเป็นสองห้องนอน\r\nได โทรมานัดชมบ้านก่อนได้ครับ ค่าประกัน 4500 ล่วงหน้าแค่เดือนเดียว', '3500', 3, 1, 'สมุทรปราการ', 'Singburin__091228225050.jpg', 'Singburin__091228225051.jpg', 'นาย', 'มังกร', 'แพรกษา 9', '0865454742', 'drogons_sleep@hotmail.com', 423, '2009-12-28 22:50:51', 1, 59);
INSERT INTO `sb_job` VALUES (67, 14, 'รับ สมัครงานคีย์ข้อมูล PART TIME รายได้ 500 บาท/วัน ', 'รับ สมัครงานคีย์ข้อมูล PART TIME รายได้ 500 บาท/วัน \r\nหจก.พาราไดซ์ ซัคเซส \r\n#รับสมัคร พนักงานคีย์ข้อมูล PART TIME\r\nลักษณะงาน	\r\n             งานคีย์ข้อมูล คีย์ข้อมูลตามเอกสารของบริษัทที่ให้ไปส่งทาง E-MAIL แก่ลูกค้าของบริษัทโดยบริษัทจะมีรายชื่อ E-MAIL ของลูกค้าตรียมไว้ให้ บริษัทไม่ได้รับทุกคนต้องมีการสัมภาษณ์งานก่อนถ้าสัมภาษณ์งานผ่านสามารถรับงานกลับไปทำได้ทันที รับงานทำที่บ้านได้ ถ้ามารับงานด้วยตัวเองมาสัปดาห์ละครั้งหรือจะให้บริษัทส่งงานไปทาง E-MAIL ก็ได้\r\nรายได้ 500 บาท/วัน ขึ้นอยู่กับปริมาณของงานบางวันอาจได้มากกว่านี้ \r\nเงินออกสัปดาห์ ละครั้งโดยออกทุกทุกวันศุกร์ของเดือน\r\n		\r\nคุณสมบัติของผู้สมัคร \r\n        - เพศ ชาย- หญิง         \r\n        - จบการศึกษา ม.3 ขึ้นไป และ อายุ 15 ปี ขึ้นไป\r\n        - มีความขยัน และรับผิดชอบต่อ งาน ที่ได้รับมอบหมาย \r\n        - มีความรู้ด้านคอมพิวเตอร์และอินเตอร์เน็ตเบื้องต้น \r\n        - ถ้ามีอินเตอร์เน็ตจะได้รับการพิจารณาเป็นพิเศษ\r\n\r\nเอกสารที่ใช้ในการสมัคร \r\n        - สำเนาบัตรประชาชน 1 ฉบับ \r\n        - สำเนาบัญชีธนาคารหน้าแรก 1 ฉบับ (ยกเว้นธนาคาร   ออมสิน และ ธกส.)  \r\n        -จดเลขที่ BM54171 เพื่อมารับเอกสารสมัครงาน\r\nสมัครด้วยตัวเองเท่านั้น\r\n\r\n   สนใจติดต่อ คุณกรวรรณ เบอร์โทรศัพท์ 089-9661779,082-4956760\r\nกรุณาโทรนัดล่วงหน้า 1 วัน เพื่อนัดวันเวลาสัมภาษณ์งาน		\r\n\r\n สำนักงานตั้งอยู่ที่ ตึกเมืองไทย-ภัทรคอมเพล็กซ์  ถ.รัชดาภิเษก ห้วยขวาง \r\n(รัชดา ซอย 18) ติดกับตลาดสุทธิสาร จังหวัด กรุงเทพ10310\r\n          (สำนักงานเปิดทำการ วันจันทร์-อาทิตย์ เวลา 11.00-19.00)	\r\n\r\nHTTP://WWW.JOBBURI.COM/TH/SHOW_JOBFULL.PHP?JOB_ID=10687&EMP_ID=10904\r\n\r\n\r\n', '500', 2, 8, 'กรุงเทพมหานคร', 'Singburin__091229200438.jpg', 'Singburin__091229200438.jpg', 'นางสาว', 'กรวรรณ ใจงาม', 'สำนักงานตั้งอยู่ที่ ตึกเมืองไทย-ภัทรคอมเพล็กซ์  ถ.รัชดาภิเษก ห้วยขวาง \r\n(รัชดา ซอย 18) ติดกับตลาดสุทธิสาร จังหวัด กรุงเทพ10310\r\n          (สำนักงานเปิดทำการ วันจันทร์-อาทิตย์ เวลา 11.00-19.00)\r\n  10130', '0899661779', 'garawan_contra