INSERT INTO `sb_job` VALUES (87, 6, 'ให้เช่า คอนโด เพียง 200 เมตร จาก BTS มาบุญครอง', 'เหมาะสำหรับคนทำงานในเมือง หรือนิสิตจุฬา หรือนักเรียนเตรียมอุดมฯ \r\nหรือ สาธิตจุฬา-สาธิต ปทุมวัน\r\n\r\nค่าเช่า 14,000 บาท/เดือน สัญญา 1 ปี\r\n\r\nคอนโดบ้านเกษมสันต์  ซอยเกษมสันต์ 1\r\n\r\n-	สระว่ายน้ำ\r\n-	ฟิตเนส\r\n-	Study room\r\n-	ระบบแสกนนิ้วเข้าออก\r\n-	สังคมดีมาก ส่วนใหญ่เป็น นิสิต +อาจารย์ จุฬา ชาวญี่ปุ่น หรือชาวต่างชาติอื่นๆ พนักงานออฟฟิศเกรดเอ\r\n\r\nห้องสตูดิโอ ชั้น 8 ขนาด  35 ตร.ม. พร้อมเฟอร์นิเจอร์บิวท์อิน\r\n\r\n-	เฟอร์นิเจอร์ Built-in\r\n-	เคาท์เตอร์ครัว\r\n-	ทีวี\r\n-	UBC\r\n-	เครื่องทำน้ำอุ่น\r\n-	เครื่องดูดฝุ่น\r\n-	แอร์\r\n-	มองเห็นมาบุญครอง และ BTS จากระเบียงห้อง\r\n\r\nติดต่อ คุณ ธีรพล โทร 081-8017053\r\n\r\n', '14,000', 3, 2, 'กรุงเทพมหานคร', '', '', 'นางสาว', 'พัชรีย์ ขจัดโจร', 'โครงการทรัพย์ทวี', '02-8163200', 'sandy_0410@live.com', 181, '2010-01-09 15:32:40', 1, 69);
INSERT INTO `sb_job` VALUES (88, 10, 'เปิดจองแล้ว  อาคารพาณิชย์ ถนนสุขสวัสดิ์-ประชาอุทิศ ', 'อาคารพาณิชย์สร้างใหม่ ติดถนนหน้าโครงการทรัพย์ทวี โฮมแฟคทอรี่ 2 ซอยคู่สร้าง มีรถสัญจรผ่านจำนวนมาก เข้า-ออก ได้ทั้งถนนประชาอุทิศและถนนสุขสวัสดิ์ ใกล้วงแหวนรอบนอก วงแหวนอุตสาหกรรม และทางด่วนสะพานแขวน  ใกล้แหล่งชุมชน โรงงานอุตสาหกรรมต่างๆ ใกล้โครงการจัดสรรต่างๆ ทั้งทาวน์เฮาส์ ที่พักอาศัย และมินิแฟคทอรี่\r\n\r\nอาคารพาณิชย์3 ชั้นครึ่ง (สามชั้น+ชั้นลอย) พร้อมที่จอดรถด้านหน้าพิเศษ จอดรถได้ 2 คันทุกห้อง มีจำนวนกัดเพียง 19 ยูนิตเท่านั้น\r\n\r\nพิเศษสุด!จองภายใน 31 ธันวาคม 52 นี้ รับส่วนลดทันที 100,000 บาท พร้อมของแถมอีกมากมาย มูลค่ากว่า 50,000 บาท ราคาสุทธิเริ่ม 1.84ล้านบาท\r\n\r\nสนใจติดต่อ โทร. 02-8163200\r\n\r\nEMAIL: INFO@BIGTHREE.CO.TH\r\n', '1,840,000', 2, 3, 'สมุทรปราการ', '', '', 'นางสาว', 'พัชรีย์ ขจัดโจร', 'โครงการทรัพย์ทวี', '02-8163200', 'sandy_0410@live.com', 292, '2010-01-09 15:36:24', 1, 69);
