INSERT INTO `sb_job` VALUES (1006, 290, 'จำหน่าย-ขายต้นกล้าแม็กคาดาเมียร์(MACADAMIA NUTS)พันธุ์660 จากศูนย์วิจัยพืชสวนเชียงราย										', 'จำหน่าย-ขายต้นกล้าแม็กคาดาเมียร์(MACADAMIA NUTS)พันธุ์660 จากศูนย์วิจัยพืชสวนเชียงราย										\r\n										\r\nต้นกล้าแม็คคาดาเมียร์ MACADAMIA NUTS พันธุ์660ได้ถูกพัฒนายีนส์ให้เหมาะแก่เมืองไทย 										\r\nเป็นถั่วที่แพงที่สุดในโลกเพราะมีรสชาดที่อร่อย ,1ไร่ปลูกได้49ต้น(ระยะปลูก 5X5 M) ของมีจำนวนจำกัด 										\r\nราคาขายขึ้นกับจำนวนซื้อ บริการจัดส่งให้ถึงที่ ขณะนี้มีต้นกล้าอายุ5เดือนสูง2ฟุต บรรจุในถุงพร้อมลงปลูก										\r\nมีจำนวนจำกัดหมดแล้วหมดเลย MACADAMIA ปลูกได้ในที่สูงตั้งแต่ระดับ 500 เมตรเหนือระดับน้ำทะเลปานกลางขึ้นไป หรือ										\r\nพื้นที่ๆมีอากาศเย็นถึงหนาวในช่วงฤดูหนาว พันธุ์600รุ่นใหม่นี้จะเริ่มให้ผลผลิตเมื่ออายุ4-5ปีเท่านั้น(ให้ผลเร็วขึ้นกว่าเดิม) 										\r\nสั่งก่อนได้ก่อน จะขายผลสุก-ผลแห้ง-อบแห้ง-ทานเอง-หรือปลูกเป็นไม้ประดับคู่บารมี ล้ำหน้า นำสมัย เอาไว้โชว์ 										\r\nไม่ตกเทรน เป็นของที่ระลึกและของฝากที่คนรับภูมิใจ สนใจติดต่อมาที่คุณเจ - J - 0814507223 ด่วน !!										\r\n', '1', 2, 8, 'กรุงเทพมหานคร', '', '', 'นาย', 'J', 'อยู่ตรงข้ามสวนหลวงร.9 ประเวศ กรุงเทพฯ (ใกล้ ม.รามคำแหง2)\r\n', '0814507223', 'wittawatkulsiri@hotmail.com', 2, '2010-09-02 06:35:57', 1, 395);
