INSERT INTO `sb_job` VALUES (1045, 363, 'ให้เช่าทาวโฮม5ชั้น ทำออฟฟิช ทำเลดีติดถนนใหญ่', 'ให้เช่าทาวโฮมทำออฟฟิช (หลังฝั่งซ้าย) ทำเลดีติด ถนนใหญ่ พระราม 5-นครอินทร์  หน้ากว้าง 5 ม.  เนื้อที่ 211 ตร.ม.  มี 5 ชั้น  4 ห้องนอน 3 ห้องน้ำ 1 ห้องครัว ชั้น 2 เป็นห้องโถงกว้าง  ให้เช่า  28,000 บ/ด สนใจติดต่อคุณจุฑาวุธ 087-6666727 btidol@gmail.com', '28,000', 3, 1, 'นนทบุรี', 'Singburin__10911174257.jpg', '', 'นาย', 'จุฑาวุธ', '124/140 ม.นนทรี 5 ถ.พระราม 5-นครอินทร์ ต.บางสีทอง อ.บางกรวย จ.นนทบุรี ', '087-6666727', 'btidol@gmail.com', 13, '2010-09-11 17:42:57', 1, 566);
INSERT INTO `sb_job` VALUES (1046, 311, 'บริษัท คาร์บอนคิงส์ จำกัด ผู้นำด้านการผลิตชิ้นส่วนอะไหล่ ลูกรีดเกลียว หล่อโลหะ รับหล่อโลหะ ซีลปั๊มน้ำ แปรงถ่าน CARBON BRUSH POWER TOOLS ชิ้นส่วนอะไหล่เครื่องจักร รับผลิตอะไหล่ที่เป็นงานโลหะทุกชนิด', '\r\n\r\nบริษัท คาร์บอนคิงส์ จำกัด ผู้นำด้านการผลิตชิ้นส่วนอะไหล่ มาตรฐาน JIS (ญี่ปุ่น), DIN (เยอรมัน), TIS (ไทย)\r\n\r\nลูกรีดเกลียว รับทำลูกรีดเกลียวใหม่ หล่อโลหะ รับหล่อโลหะ ผู้ผลิตจำหน่ายซีลปั๊มน้ำ แปรงถ่าน (CARBON BRUSH) \r\n\r\n\r\nผู้ผลิตแปรงถ่านอุตสาหกรรม และ CARBON BRUSH POWER TOOLS ผลิต ชิ้นส่วนอะไหล่เครื่องจักร รับผลิตอะไหล่ที่เป็นงานโลหะทุกชนิด\r\n\r\n\r\n\r\n	ลูกรีดเกลียว รับทำลูกรีดเกลียวใหม่ รับซ่อมลูกรีดเกลียวจำหน่าย ลูกรีด และ อุปกรณ์ ลูกรีด (ROLLER) สำหรับงานรีด \r\n                ลวดทองแดง, รีดเหล็กแผ่น, รีดท่อ MACHINING GRINDING LAPPING SURFACE \r\n\r\n	หล่อโลหะ รับหล่อโลหะ เราคือผู้ให้บริการงานด้านการผลิตชิ้นส่วนเครื่องจักรกลทุกอุตสาหกรรม รับหล่อโลหะตามแบบของลูกค้า \r\n                ด้วยวัสดุเหล็กหล่อ เหล็กหล่อเหนียว อลูมิเนียม สแตนเลส ทองเหลืองเกรดพิเศษ BRONZE CASTING งานเฟือง อลูมิเนียมบรอนซ์ ROTARY JOINT\r\n\r\n	ผู้ผลิตจำหน่ายซีลปั๊มน้ำ แมคคานิคคอล MECHANICAL SEAL (ซีลปั๊มน้ำ)\r\n\r\n	แปรงถ่าน (CARBON BRUSH) ผู้ผลิตแปรงถ่านอุตสาหกรรม และ CARBON BRUSH POWER TOOLS รับผลิตแปรงถ่านทุกขนาด \r\n                ตามแบบของลูกค้า ขายแปรงถ่าน คาร์บอน แกรไฟต์ ถ่านมอเตอร์ ซองแปรงถ่าน BRUSH HOLDER รับออกแบบ - ผลิต และให้คำปรึกษา \r\n                แปรงถ่านมอเตอร์ (CARBON BRUSH) ซองแปรงถ่านมอเตอร์ (BRUSH HOLDER) และ ชิ้นส่วนอุปกรณ์จากคาร์บอน(CARBON PART) อะไหล่ของมอเตอร์\r\n\r\n	ผลิต ชิ้นส่วนอะไหล่เครื่องจักร รับผลิตอะไหล่ที่เป็นงานโลหะทุกชนิด\r\n\r\nบริษัท คาร์บอน คิงส์ จำกัด \r\n511/5 ม.1 ถ.ประชาอุทิศ แขวงทุ่งครุ เขตทุ่งครุ กรุงเทพฯ 10140 \r\nโทร. 0-2873-4700, 0-2873-5555 \r\nแฟกซ์. 0-2426-1618 \r\nE-MAIL : SALES@CARBONKINGS.CO.TH         \r\nWEBSITE : HTTP://WWW.CARBONKINGS.CO.TH/INDEX.PHP\r\n\r\nการติดต่อทาง E-MAIL กรุณาติดต่อที่ \r\n\r\nSALES@CARBONKINGS.CO.TH \r\n\r\nเท่านั้นนะค่ะ\r\n\r\n\r\n\r\nคาร์บอน ,ผู