INSERT INTO `sb_job` VALUES (1057, 91, 'ให้เช่า คอนโด LPN สาทรเพลส ตึก D ชั้น5 ถ.นราธิวาสฯ ติดแมคโคร สาทร', 'คอนโด ขนาด 30 ตร.ม. ห้อง studio เป็นคอนโดติดถนน นราธิวาสเลยค่ะ\r\nตกแต่งแล้ว พร้อมเฟอร์นิเจอร์ (เตียง ที่นอน โซฟา ตู้เสื้อผ้า โตีะ เก้าอี้ ชั้นวางของ)\r\nและอุปกรณ์อำนวยความสะดวก (แอร์ ตู้เย็น ทีวี ไมโครเวฟ) ที่จอดรถ 1 คัน และสมาชิกฟิตเนส\r\nเดินทางไปสีลม สาทร รถไฟฟ้าสถานช่องนนทรีสะดวก\r\nแถมติด แมคโคร เดินไปได้เลยค่ะ ใกล้มากๆๆ สะดวกทั้งการจับจ่ายซื้อของที่จำเป้นและการเดินทางไปย่านต่างๆ\r\nเช่น สยาม สีลม พระราม3\r\n\r\n\r\nสถานที่: คอนโด LPN สาทรเพลส ตึก D ชั้น5\r\nการคมนาคม: ใกล้สถานีรถไฟฟ้าช่องนนทรี\r\n- 5-10 นาที ไปเซนทรัลพระราม 3 และโลตัส พระราม3\r\n- ติด Macro สาทร (เดินไปได้)\r\n- BRT เปิดใช้ในปีนี้ (สถานีอยู่ใกล้คอนโนเพียง 100 เมตร)\r\n\r\nพร้อมสระว่ายน้ำ ซาวน่า และ fitness center\r\nเช่า ราคา 12,000 บาท/เดือน \r\nพร้อมเฟอร์นิเจอร์ แอร์ โต๊ะอาหาร เตียง ตู้เสื้อผ้า ตู้เย็น TV\r\nเข้าอยู่ทันที\r\n\r\n30-sqm studio for rent at LPN Narathiwas-Satorn (next to Makro). Building D, 5th floor.: fully furnished\r\n\r\nBuilding D, 5 Floor.\r\nNear by makro sathorn,\r\nWalking distance to Makro\r\n5-10 mins to Central Rama3 Mall, and Lotus Rama3\r\n10 mins to chongnonsri BTS station\r\nwith swimming pool, sauna, and fitness center\r\nfor rent 12,000 baht/month\r\nContact: 084-145-6600\r\nราคา : 12000 บาท \r\nประกาศโดย : bee | Email : thebluewater@gmail.com | Tel : 0841456600\r\nที่อยู่ :สาทร\r\n', '12000', 3, 2, 'กรุงเทพมหานคร', 'Singburin__10913153232.jpg', 'Singburin__10913153232.jpg', 'นางสาว', 'JOY JOY', 'กทม  10120', '0846429123', 'nonghyoy@gmail.com', 25, '2010-09-13 15:32:32', 1, 569);
