INSERT INTO `webboard` VALUES (7, 2, 'ให้เช่า คอนโด ลุมพินี รามอินทรา-นวมินทร์ ชั้นสูง วิวสวย 5,000-6,500บ./เดือน', 'ให้เช่า คอนโด ลุมพินี รามอินทรา-นวมินทร์ ชั้นสูง วิวสวย 5,000-6,500บ./เดือน\r\nตรงข้ามสหฟาร์ม ใกล้ รพ.สินแพทย์ ตลาดรามอินทรา กม.8 ห้างแฟชั่นไอส์แลนด์ และ เทสโก้โลตัส สุขาภิบาล 1\r\n\r\nฟรี ค่้าน้ำ-ค่าไฟ ไปจนถึง มกราคม 2554\r\nประกัน 2เดือน\r\nล่วงหน้า 1เดือน\r\n*ค่าไฟ จ่ายไฟหลวงไม่มีชาร์ตค่ะ^_^V\r\n\r\nคอนโดหรู ชั้นสูง วิวเมือง สวยมากค่ะ ระเบียงไม่ชนกับห้องคนอื่น ลมดี ไม่ร้อน\r\nมือแรกเลยนะคะ^_^ พร้อมเข้าอยู่ได้เลยค่ะ\r\nแอร์ 2เครื่อง เครื่องทำน้ำอุ่น และเครื่องกรองน้ำดื่ม 4ขั้นตอน เพื่อการบริโภค\r\n(ประหยัดค่าน้ำดื่ม เพราะ มีน้ำดื่มสะอาดไว้ดื่มตลอด ไม่ต้องซื้อ)\r\n\r\nภายในโครงการ สะอาด ปลอดภัย เข้า-ออก ระบบคีย์การ์ด\r\nมีสวนพักผ่อน สนามกีฬา ฟิตเนส สนามกีฬา และร้านค้าต่างๆ\r\n\r\n\r\n\r\nสนใจติดต่อ 081-8097633\r\n* ต่อรองราคากันได้นะคะ เจ้าของห้องใจดีค่ะ^_^\r\nHTTP://WWW.DDPROPERTY.COM/PROPERTY/SHOW/91595\r\nHTTP://WWW.THAIHOMELIST.COM/VIEW.PHP?LG=TH&ID=34046\r\n\r\n\r\nหมายเหตุ : ติดต่อขอชมห้อง ได้ในวันเสาร์-อาทิตย์ นะคะ เพราะวันธรรมดาทำงานและอยู่ไกลค่ะ +_+\r\n', 2, 'Singburin__10928143931.JPG', '', 27, '2010-09-28 14:39:38', 1, 604);
